import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Color.css";
import Spinner from "react-bootstrap/Spinner";

const Color = () => {
  const [colors, setColors] = useState();

  const url = "https://x-colors.herokuapp.com/api/random?number=7";

  async function fetchColors() {
    await axios
      .get(url)
      .then((res) => {
        setColors(res.data);
      })
      .catch((error) => console.log(error));
  }

  useEffect(() => {
    // fetchColors();
  }, []);

  function invertColor(hex) {
    if (hex.indexOf("#") === 0) {
      hex = hex.slice(1);
    }

    if (hex.length === 3) {
      hex = hex[0] + hex[0] + hex[1] + hex[1] + hex[2] + hex[2];
    }
    if (hex.length !== 6) {
      throw new Error("Invalid HEX color.");
    }

    var r = (255 - parseInt(hex.slice(0, 2), 16)).toString(16),
      g = (255 - parseInt(hex.slice(2, 4), 16)).toString(16),
      b = (255 - parseInt(hex.slice(4, 6), 16)).toString(16);

    return "#" + padZero(r) + padZero(g) + padZero(b);
  }

  function padZero(str, len) {
    len = len || 2;
    var zeros = new Array(len).join("0");
    return (zeros + str).slice(-len);
  }

  return (
    <>
      {colors !== undefined ? (
        colors.map((color) => (
          <div className="container">
            <div
              className="color-boxes"
              onClick={fetchColors}
              style={{
                disaply: "flex",
                flexDirection: "row",
                background: color.hex,
                margin: "1em auto",
                border: ".25em black solid",
                padding: ".5em",
                borderRadius: "50%",
                height: "300px",
                width: "300px",
                textAlign: "center",
              }}
            >
              <h3
                style={{ marginTop: "3em", color: invertColor(color.hex) }}
              >{`HEX: ${
                color.hex
              } || ${color.hsl.toUpperCase()} || ${color.rgb.toUpperCase()}`}</h3>
            </div>
          </div>
        ))
      ) : (
        <Spinner
          className="spinner"
          animation="grow"
          role="status"
          size="lg"
          style={{
            color: "red",
            disaply: "inline",
            marginLeft: "40%",
            marginTop: "20%",
            height: "10em",
            width: "10em",
          }}
        ><span style={{color: "teal", marginLeft: "2em", textAlign: "center", marginBottom: "-50%"}}>Be right there..</span></Spinner>
      )}
    </>
  );
};

export default Color;
