import React, { useState, useEffect } from "react";
import axios from "axios";

const Color = () => {
  const [colors, setColors] = useState();

  async function fetchColors() {
    await axios
      .get(url)
      .then((res) => {
        setColors(res.data);
      })
      .catch((error) => console.log(error));
  }

  useEffect(() => {
    fetchColors();
  }, []);

  const url = "https://x-colors.herokuapp.com/api/random?number=7";

  return (
    <>
      {colors !== undefined ? (
        colors.map((color) => (
          <>
            <h1>{`HEX: ${
              color.hex
            } || ${color.hsl.toUpperCase()} || ${color.rgb.toUpperCase()}`}</h1>
            <div
              className="color-boxes"
              style={{
                background: color.hex,
                display: "flex",
                flexDirection: "row",
                justifyContent: "space-around",
                width: "40em auto",
                height: "6.25em ",
                margin: "1em",
                border: ".25em black solid",
                alignContent: "flex-start",
              }}
            ></div>
          </>
        ))
      ) : (
        <p>Loading...</p>
      )}
      <button onClick={fetchColors}>New Colors</button>
    </>
  );
};

export default Color;
